module Support

  module FoursquareMock
    include WebMock::API
    include FoursquareClient

    # Mock a foursquare venue search request.
    # Returns the search params used to construct the request.
    def mock_foursquare_venue_search
      params = { ll: '40.7%2C-74' }.with_indifferent_access

      # Use the Faraday object to build our request data for us
      request = foursquare_client.connection.build_request 'get' do |req|
        req.url "venues/search", params
      end
      url = request.to_env(foursquare_client.connection).url.to_s

      stub_request(:get, url).to_return status: 200,
                                        body: Responses.venue_search_body,
                                        headers: {
                                          'x-ratelimit-limit' => 5000,
                                          'x-ratelimit-remaining' => 4995
                                        }

      params
    end

    # Mock a foursquare venue request. Returns the faked foursquare
    # id with which a request can be made to return this mock.
    def mock_foursquare_venue
      params = { id: '4beb2649b3352d7f2f1b56d2' }.with_indifferent_access

      # Use the Faraday object to build our request data for us
      request = foursquare_client.connection.build_request 'get' do |req|
        req.url "venues/#{params[:id]}"
      end
      url = request.to_env(foursquare_client.connection).url.to_s

      stub_request(:get, url).to_return status: 200,
                                        body: Responses.venue_body,
                                        headers: {
                                          'x-ratelimit-limit' => 5000,
                                          'x-ratelimit-remaining' => 4995
                                        }

      params
    end

    # Mock a foursquare venue request for a venue that doesn't exist, expecting
    # a response mocking an error response that foursquare would return.
    #
    # The id to use in a request is returned.
    def mock_foursquare_venue_not_found
      params = { id: '123_doesnt_exist' }.with_indifferent_access

      # Use the Faraday object to build our request data for us
      request = foursquare_client.connection.build_request 'get' do |req|
        req.url "venues/#{params[:id]}"
      end
      url = request.to_env(foursquare_client.connection).url.to_s

      stub_request(:get, url).to_return status: 400,
                                        body: Responses.venue_not_found_body,
                                        headers: {
                                          'x-ratelimit-limit' => 5000,
                                          'x-ratelimit-remaining' => 4995
                                        }

      params
    end

