module Support

  module FoursquareMock
    include WebMock::API

    # Mock a foursquare venue search request.
    # Returns the search params used to construct the request.
    def mock_foursquare_venue_search
      client = Foursquare2::Client.new foursquare_credentials
      params = { ll: '40.7%2C-74', v: '20140510' }

      # Use the Faraday object to build our request data for us
      request = client.connection.build_request 'get' do |req|
        req.url "venues/search", params
      end
      url = request.to_env(client.connection).url.to_s

      stub_request(:get, url).to_return status: 200,
                                        body: Responses.venue_search_body,
                                        headers: {
                                          'x-ratelimit-limit' => 5000,
                                          'x-ratelimit-remaining' => 4995
                                        }

      params
    end

    def foursquare_credentials
      @creds ||= {
        client_id: Rails.application.secrets.foursquare['client_id'],
        client_secret: Rails.application.secrets.foursquare['client_secret']
      }
    end

